export const ACT_EMAIL = 'ACT_EMAIL';
export const INICIAL_REQ = 'INICIAL_REQ';
export const GET_API = 'GET_API';

export const actEmail = (payload) => ({ type: ACT_EMAIL, payload });

export const inicialRequest = () => ({ type: INICIAL_REQ });

export const responseApi = (payload) => ({ type: GET_API, payload });

export const getRequest = () => async (dispatch) => {
  try {
    dispatch(inicialRequest());
    const response = await fetch('https://economia.awesomeapi.com.br/json/all');
    const result = await response.json();
    const resultKeys = Object.keys(result);
    resultKeys.splice(1, 1);
    dispatch(responseApi(resultKeys));
  } catch (e) {
    throw new Error(e);
  }
};
