import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { exDelete } from '../redux/actions';
import '../styles/table.css';

class Table extends Component {
  handleDelete = () => {
    const { exDelete, expenses } = this.props;
    const deletar = expenses.splice(1);
    return exDelete(deletar);
  };

  render() {
    const { expenses } = this.props;
    return (
      <table className="table">
        <thead className="thead">
          <tr>
            <th>Descrição</th>
            <th>Tag</th>
            <th>Método de pagamento</th>
            <th>Valor</th>
            <th>Moeda</th>
            <th>Câmbio utilizado</th>
            <th>Valor convertido</th>
            <th>Moeda de conversão</th>
            <th>Editar/Excluir</th>
          </tr>
        </thead>
        { expenses.map((e) => (
          <tbody key={ e.id }>
            <tr className="conversao">
              <td>{ e.description }</td>
              <td>{ e.tag }</td>
              <td>{ e.method }</td>
              <td>{ `R$ ${Number(e.value).toFixed(2)}`}</td>
              <td>{ (e.exchangeRates[e.currency].name) }</td>
              <td>{`R$ ${Number(e.exchangeRates[e.currency].ask).toFixed(2)}`}</td>
              <td>
                { `R$ ${(Number(e.value) * Number(e.exchangeRates[e.currency]
                  .ask)).toFixed(2)}` }
              </td>
              <td>Real</td>
              <td>
                <button
                  type="button"
                  data-testid="delete-btn"
