import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetcheExpense, actExpenses } from '../redux/actions';
import api from '../redux/reducers/api';

class WalletForm extends Component {
  state = {
    id: 0,
    value: '',
    payment: 'Dinheiro',
    tag: 'Alimentação',
    description: '',
    currency: 'USD',
  };

  async componentDidMount() {
    const { dispatch } = this.props;
    dispatch(fetcheExpense);
  }

  handleSubmit = async () => {
    const { dispatch, expenses } = this.props;
    const local = await api();
    const { description, tag, currency, value, payment } = this.state;
    const expense = { description,
      tag,
      value,
      currency,
      payment,
      id: expenses.length,
      local };
    await dispatch(actExpenses(expense));
    this.setState({ value: '', description: '' });
  };

  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value });
  };

  render() {
    const { value, currency, payment, tag, description } = this.state;
    const { currencies } = this.props;
    return (
      <form>
        <label htmlFor="input-value">
          Valor:
          <input
            id="input-value"
            name="value"
            value={ value }
            data-testid="value-input"
            onChange={ this.handleChange }
          />
        </label>

        <label htmlFor="description-input">
          Descrição:
          <input
            id="description-input"
            name="description-input"
            value={ description }
            data-testid="description-input"
            onChange={ this.handleChange }
          />
        </label>

        <label htmlFor="coin">
          Moeda:
          <select
            id="coin"
            name="currency"
            value={ currency }
            data-testid="currency-input"
            onChange={ this.handleChange }
          >
            {currencies
              .map((e, index) => <option key={ index }>{ e }</option>)}
          </select>
        </label>

        <label htmlFor="payment">
          Forma de Pagamento:
          <select
            id="payment"
            type="select"
            name="payment"
            data-testid="method-input"
            onChange={ this.handleChange }
            value={ payment }
          >
            <option value="Dinheiro">Dinheiro</option>
            <option value="Cartão de crédito">Cartão de crédito</option>
            <option value="Cartão de débito">Cartão de débito</option>
          </select>
        </label>

        <label htmlFor="tag-input">
          Tag:
          <select
            id="tag-input"
            type="select"
            name="tag"
            data-testid="tag-input"
            onChange={ this.handleChange }
            value={ tag }
          >
            <option>Alimentação</option>
            <option>Lazer</option>
            <option>Trabalho</option>
            <option>Transporte</option>
            <option>Saúde</option>
          </select>
        </label>

        <button
          type="button"
          name="button"
          onClick={ this.handleSubmit }
        >
          Adicionar despesa
        </button>
      </form>
    );
  }
}

const mapStateToProps = (state) => ({
  currencies: state.wallet.currencies,
  expenses: state.wallet.expenses,
});

WalletForm.propTypes = {
  currencies: PropTypes.arrayOf(PropTypes.string),
  api: PropTypes.func,
}.isRequired;

export default connect(mapStateToProps, mapDispatchToProps)(WalletForm);
