import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

class Header extends Component {
  handleSubmit = () => {
    const { expenses } = this.props;
    const tot = expenses.reduce((acc, nextElement) => {
      let total = acc;
      const accCurr = nextElement.currency;
      total += Number(nextElement.exchangeRates[accCurr].ask * nextElement.value);
      return Number(total.toFixed(2));
    }, 0);
    return tot;
  };

  render() {
    const { email, expenses } = this.props;
    return (
      <header>
        <div className="logo-header">
          <img className="logo" src="https://cdn-images-1.medium.com/max/1200/1*_8rYOyJj9qiQsmeoVk3Gfg.png" alt="logo-trybe" />
          <span className="trybe">TRYBE</span>
          <span className="wallet">Wallet</span>
        </div>

        <div className="total">
          <p className="name-and-email">Email:</p>
          <p data-testid="email-field">{ email }</p>
        </div>

        <div className="despesa">
          <p>Despesa Total:</p>
          <p data-testid="total-field">
            { expenses.length === 0 ? 0 : this.handleSubmit() }
          </p>
          <p data-testid="header-currency-field">BRL</p>
        </div>
      </header>
    );
  }
}

Header.propTypes = { email: PropTypes.string,
  expenses: PropTypes.arrayOf(PropTypes.object) }.isRequired;

const mapStateToProps = (state) => ({ email: state.user.email,
  expenses: state.wallet.expenses });

export default connect(mapStateToProps)(Header);
