import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

// const total = 0;
class Header extends Component {
  getTotalValue = () => {
    const { expenses } = this.props;

    const totalValue = expenses.reduce((acc, expense) => {
      const { value, currency, exchangeRates } = expense;
      const { ask } = exchangeRates[currency];

      const convertedValue = value * ask;

      return acc + convertedValue;
    }, 0);

    // https://www.delftstack.com/pt/howto/javascript/javascript-round-to-2-decimal-places/
    const convertedTotalValue = Math.round((totalValue + Number.EPSILON) * 100) / 100;

    return convertedTotalValue;
  };

  render() {
    const { email } = this.props;
    return (
      <header>
        <div className="logo-header">
          <img className="logo" src="https://cdn-images-1.medium.com/max/1200/1*_8rYOyJj9qiQsmeoVk3Gfg.png" alt="logo-trybe" />
          <span className="trybe">TRYBE</span>
          <span className="wallet">Wallet</span>
        </div>

        <div className="total">
          <p className="name-and-email">Email:</p>
          <p data-testid="email-field">{ email }</p>
        </div>

        <div className="despesa">
          <p>Despesa Total:</p>
          <p data-testid="total-field">{ total.toFixed(2) }</p>
          <p data-testid="header-currency-field">BRL</p>
        </div>
      </header>
    );
  }
}

Header.propTypes = { email: PropTypes.string,
  expenses: PropTypes.arrayOf(PropTypes.any) }.isRequired;

const mapStateToProps = (state) => ({ email: state.user.email,
  expenses: state.wallet.expenses,
});
